import Foundation

func range(start: Int = 0, stop: Int, by: Int = 1) -> StrideTo<Int> {
    return stride(from: start, to: stop, by: by)
}


func getMatrixAnswer(number: Int) -> [Double] {
    var answArr = Array<Double>(repeating: 0, count: 10)
    answArr[number] = 1
    return answArr
}

func getNumberFromMatrix(numbers: [Double]) -> Int {
    for i in range(stop: 10) {
        if numbers[i] == 1 {
            return i
        }
    }
    return -1
}

